const Files = this.getDBM().Files.data;
const separator = new RegExp(Files.settings.separator,"g");
const args = msg.content.split(separator);
const prefixMention = new RegExp(`^<@!?${client.user.id}>( |)$`);
if (args[0].match(prefixMention)){
  const allData = Files.commands;
  let cmd = false;
  Object.keys(allData).forEach(function(command) {
    if (allData[command]) {
      if (allData[command].name == args[1]) {
        cmd = allData[command];
      }
    }
  });
  if (cmd) {
    let restriction = true;
    switch (parseInt(cmd.restriction)) {
      case 0:
        restriction = false;
        break;
      case 1:
        if (cache.server) {
          if (cmd.permissions != "NONE") {
            if (member.permissions.has(cmd.permissions)) {
              restriction = false;
            }
          } else {
            restriction = false;
          }
        }
        break;
      case 2:
      case 4:
        if (msg.author.id == Files.settings.ownerId) {
          restriction = false;
        };
        break;
      case 3:
        if (cache.server) {
          restriction = false;
        };
        break;
    };
    if (!restriction) {
      args.shift();
      args[0] = Files.settings.tag + args[0]
      cache.msg.content = args.join(" ");
      const cache2 = {
        actions: cmd.actions,
        index: -1,
        temp: {},
        server: cache.server,
        msg: cache.msg
      }
      this.callNextAction(cache2);
    }
  }
}
